mapper.select:
select 'false' as QUERYID,

from balance
where stockcode = #{stockcode,jdbcType=CHAR}
  and reportdate = #{reportdate,jdbcType=INTEGER}

mapper.delete:
delete from balance
where stockcode = #{stockcode,jdbcType=CHAR}
  and reportdate = #{reportdate,jdbcType=INTEGER}

mapper.select:
select * from balance where stockcode in

  #{code}

and (`quarter` = #{quarter} or reportdate > #{reportdate})

select stockcode,reportdate,

   ${column}

from balance where stockcode in

  #{code}

and (`quarter` = #{quarter} or reportdate > #{reportdate})
