mapper.select:
select 'false' as QUERYID,

from cash
where stockcode = #{stockcode,jdbcType=CHAR}
  and enddate = #{enddate,jdbcType=INTEGER}

mapper.delete:
delete from cash
where stockcode = #{stockcode,jdbcType=CHAR}
  and enddate = #{enddate,jdbcType=INTEGER}

mapper.select:
select * from cash where stockcode in

  #{code}

and (`quarter` = #{quarter} or enddate > #{enddate})

select stockcode,enddate,

 ${column}

from cash where stockcode in

  #{code}

and (`quarter` = #{quarter} or enddate > #{enddate})
